INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_body`, `field_articleSummary`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('2', '2', 'en_us', 'Welcome to Jackmcgreevy.com!', '<p>It’s true, this site doesn’t have a whole lot of content yet, but don’t worry. Our web developers have just installed the CMS, and they’re setting things up for the content editors this very moment. Soon Jackmcgreevy.com will be an oasis of fresh perspectives, sharp analyses, and astute opinions that will keep you coming back again and again.</p>', NULL, '2015-07-05 22:20:07', '2015-07-05 22:20:07', '3a6d7326-ef07-4c27-bd3d-39c5f1cd8054');
